import React,{useState} from 'react';
import {View,Text,Pressable,TextInput,StyleSheet} from 'react-native';
import useLocale from '../hooks/useLocale';

const ShoppingForm = (props) => {
	
	const locale = useLocale();

	const [state,setState] = useState({
		type:"",
		count:"",
		price:""
	})
	
	const addToList = () => {
		let item = {
			...state,
			id:0
		}
		props.addToList(item);
		setState({
			type:"",
			count:"",
			price:""			
		})
	}

	return(
		<View style={styles.container}>
			<View style={styles.row}>
				<Text style={[styles.text,styles.label]}>{locale.strings.type}:</Text>
				<TextInput style={[styles.text,styles.input]}
					onChangeText={(text) => setState((state) => {
						return {
							...state,
							type:text
						}
					})
					
					} value={state.type}/>
			</View>
			<View style={styles.row}>
				<Text style={[styles.text,styles.label]}>{locale.strings.count}:</Text>
				<TextInput style={[styles.text,styles.input]}
					onChangeText={(text) => setState((state) => {
						return {
							...state,
							count:text
						}
					})				
					} value={state.count} keyboardType="numeric"/>
			</View>
			<View style={styles.row}>
				<Text style={[styles.text,styles.label]}>{locale.strings.price}:</Text>
				<TextInput style={[styles.text,styles.input]}
					onChangeText={(text) => setState((state) => {
						return {
							...state,
							price:text
						}
					})				
					} value={state.price} keyboardType="numeric"/>
			</View>
			<View style={styles.buttonRow}>
				<Pressable style={styles.addButton} 
					onPress={addToList}>
					<Text style={styles.text}>{locale.strings.add}</Text>
				</Pressable>
			</View>
		</View>
	)
}

const styles = StyleSheet.create({
	container:{
		flex:1,
		alignItems:"center",
		justifyContent:"center",
	},
	row:{
		flex:1,
		flexDirection:"row",
		alignItems:"center",
		justifyContent:"space-between"
	},
	buttonRow:{
		flex:1,
		flexDirection:"row",
		alignItems:"center",
		justifyContent:"center"
	},
	addButton:{
		height:80,
		width:110,
		backgroundColor:"blue",
		alignItems:"center",
		justifyContent:"center"
	},
	text:{
		fontFamily:"sans-serif",
		fontSize:18
	},
	label:{
		fontWeight:"bold"
	},
	input:{
		width:200,
		backgroundColor:"lightgreen"
	}
})

export default ShoppingForm;
